function handleDraw(){var e=parseInt(document.getElementById("numN").value,10),t=parseInt(document.getElementById("numX").value,10);isValidInput(e,t)?displayMessage("選ばれた番号: "+drawLottery(e,t).join(", ")):displayMessage("無効な入力です。1-100の範囲で正しい数を入力してください。",!0)}function isValidInput(e,t){return!isNaN(e)&&!isNaN(t)&&1<=e&&e<=100&&1<=t&&t<=100&&t<=e}function drawLottery(e,t){var n=Array.from({length:e},(e,t)=>t+1),a=[];for(let e=0;e<t;e++){var r=Math.floor(Math.random()*n.length);a.push(n.splice(r,1)[0])}return a}function setupButtonHandlers(e){e.forEach(({buttonClass:e,handler:t})=>{document.querySelector(e).addEventListener("click",t)})}function displayMessage(e,t=!1){var n=document.getElementById("result");n.innerText=e,n.style.color=t?"red":"black"}document.getElementById("drawButton").addEventListener("click",handleDraw);