function handleDraw(){const e=parseInt(document.getElementById("numN").value,10),t=parseInt(document.getElementById("numX").value,10);if(!isValidInput(e,t))return void displayMessage("無効な入力です。1-100の範囲で正しい数を入力してください。",!0);displayMessage(`選ばれた番号: ${drawLottery(e,t).join(", ")}`)}function isValidInput(e,t){return!isNaN(e)&&!isNaN(t)&&e>=1&&e<=100&&t>=1&&t<=100&&t<=e}function drawLottery(e,t){const n=Array.from({length:e},((e,t)=>t+1)),a=[];for(let e=0;e<t;e++){const e=Math.floor(Math.random()*n.length);a.push(n.splice(e,1)[0])}return a}function setupButtonHandlers(e){e.forEach((({buttonClass:e,handler:t})=>{document.querySelector(e).addEventListener("click",t)}))}function displayMessage(e,t=!1){const n=document.getElementById("result");n.innerText=e,n.style.color=t?"red":"black"}document.getElementById("drawButton").addEventListener("click",handleDraw);